        base_path = '/'.join(base_path)

        parent = CorpPage.objects.get(full_path=base_path) if base_path else None
        page, _ = CorpPage.objects.get_or_create(parent=parent, slug=slug)
        page.full_path = path
        page.is_locked = True
        page.save()


